import React, {Component} from "react"
import {MDBContainer, MDBCol, MDBJumbotron, MDBRow, MDBAnimation} from "mdbreact";
import ReactWOW from "react-wow";

class Skills extends Component {
    constructor(props) {
        super(props)
        this.createTableItem = this.createTableItem.bind(this)
        this.setExpBarColor = this.setExpBarColor.bind(this)
    }

    createTableItem(language, tools, width) {
        return (
            <ReactWOW animation="slideInLeft">
            <tr>
                <th scope="row">{language}</th>
                <td>{tools}</td>
                <td>
                    {this.setExpBarColor(width)}
                </td>
            </tr>
            </ReactWOW>
        )
    }

    setExpBarColor(width) {
        if (width > 75) {
            return (<div className="meter"><span style={{width: " " + width + "%"}}/></div>)
        } else if (width <= 75 && width >= 60) {
            return (<div className="meter orangeSpan"><span style={{width: " " + width + "%"}}></span></div>)
        } else {
            return (<div className="meter redSpan"><span style={{width: " " + width + "%"}}></span></div>)
        }
    }


    render() {
        return (
            <div className="elegant-color-dark">
                <MDBRow center style={{height: "15vh"}}>
                    <MDBCol size="4">
                        <MDBContainer className="flex-center">
                            <h1 className="text-light">Skills</h1>
                        </MDBContainer>
                    </MDBCol>
                    <MDBCol size="8"/>
                </MDBRow>
                <MDBRow center>
                    <MDBCol size="10">
                        <MDBJumbotron className="elegant-color">
                            <MDBContainer fluid>
                                <h3 className="text-light">Programming Languages</h3>
                                <table className="table table-striped table-hover table-bordered table-dark">
                                    <thead className="thead-dark">
                                    <tr>
                                        <th style={{width: "10%"}} scope="col">Language</th>
                                        <th style={{width: "60%"}} scope="col">Libraries Frameworks, And Tools Used</th>
                                        <th style={{width: "25%"}} scope="col">Experience</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {this.createTableItem("Java", "Spring, Spark, JUint, Android", 95)}
                                    {this.createTableItem("C++", "Qt", 80)}
                                    {this.createTableItem("C", "N/A", 60)}
                                    {this.createTableItem("Python", "Numpy, Pandas, PyTorch", 90)}
                                    {this.createTableItem("SQL", "Microsoft SQL Server, MySQL, Mongoose", 75)}
                                    {this.createTableItem("HTML", "N/A", 90)}
                                    {this.createTableItem("CSS", "N/A", 90)}
                                    {this.createTableItem("JS/JSX", "React, Express, Node", 55)}
                                    {this.createTableItem("PHP", "N/A", 80)}
                                    {this.createTableItem("Solidity", "MetaMask, Rinkeby", 65)}
                                    {this.createTableItem("Arduino", "N/A", 80)}
                                    {this.createTableItem("Dart", "Flutter", 25)}
                                    {this.createTableItem("Bash", "N/A", 75)}
                                    </tbody>
                                </table>

                            </MDBContainer>
                        </MDBJumbotron>
                        <MDBJumbotron className="elegant-color">
                            <h4 className="text-light">Other Tools And Concepts I'm Familiar With:</h4>
                            <ul className="tools text-light">
                                <MDBRow>
                                    <MDBCol size="6">
                                        <li>Scrum</li>
                                        <li>Maven</li>
                                        <li>npm</li>
                                        <li>Yarn</li>
                                        <li>Agile Development</li>
                                        <li>Black Box Testing</li>
                                        <li>Test Driven Development</li>
                                        <li>Machine Learning</li>
                                        <li>Basic Penetration Testing</li>
                                        <li>Basic Cryptography</li>
                                        <li>JSON</li>
                                    </MDBCol>
                                    <MDBCol size="6">
                                        <li>Universal Modeling Language</li>
                                        <li>LucidChart</li>
                                        <li>Socket Layer Networking</li>
                                        <li>RESTful Web Services</li>
                                        <li>Full Stack Development</li>
                                        <li>Git</li>
                                        <li>Hyperledger</li>
                                        <li>BlockChain</li>
                                        <li>Microsoft Office</li>
                                        <li>Google Cloud Platform</li>
                                        <li>Docker</li>
                                    </MDBCol>
                                </MDBRow>
                            </ul>
                        </MDBJumbotron>
                    </MDBCol>
                </MDBRow>
            </div>)
    }
}

export default Skills
